ineNumber:1,columnNumber:85},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-6f2f4cd2-b300-48b2-9a7e-3bbeeae4f492.mdx",lineNumber:1,columnNumber:79},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-6f2f4cd2-b300-48b2-9a7e-3bbeeae4f492.mdx",lineNumber:1,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-6f2f4cd2-b300-48b2-9a7e-3bbeeae4f492.mdx",lineNumber:21,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-6f2f4cd2-b300-48b2-9a7e-3bbeeae4f492.mdx",lineNumber:19,columnNumber:1},this),(0,n.jsxDEV)(C,{value:"manual",children:(0,n.jsxDEV)(w,{children:[(0,n.jsxDEV)(g,{children:"Copy and paste the following code into your project."},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-6f2f4cd2-b300-48b2-9a7e-3bbeeae4f492.mdx",lineNumber:31,columnNumber:1},this),(0,n.jsxDEV)(B,{name:"animated-subscribe-button",children:(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import React, { useState } from "react";
import { AnimatePresence, motion } from "framer-motion";

interface AnimatedSubscribeButtonProps {
  buttonColor: string;
  buttonTextColor?: string;
  subscribeStatus: boolean;
  initialText: React.ReactElement | string;
  changeText: React.ReactElement | string;
}

export const AnimatedSubscribeButton: React.FC<
  AnimatedSubscribeButtonProps
> = ({
  buttonColor,
  subscribeStatus,
  buttonTextColor,
  changeText,
  initialText,
}) => {
  const [isSubscribed, setIsSubscribed] = useState<boolean>(subscribeStatus);

  return (
    <AnimatePresence mode="wait">
      {isSubscribed ? (
        <motion.button
          className="relative flex w-[200px] items-center justify-center overflow-hidden rounded-md bg-white p-[10px] outline outline-1 outline-black"
          onClick={() => setIsSubscribed(false)}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <motion.span
            key="action"
            className="relative block h-full w-full font-semibold"
            initial={{ y: -50 }}
            animate={{ y: 0 }}
            style={{ color: buttonColor }}
          >
            {changeText}
          </motion.span>
        </motion.button>
      ) : (
        <motion.button
          className="relative flex w-[200px] cursor-pointer items-center justify-center rounded-md border-none p-[10px]"
          style={{ backgroundColor: buttonColor, color: buttonTextColor }}
          onClick={() => setIsSubscribed(true)}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <motion.span
            key="reaction"
            className="relative block font-semibold"
            initial={{ x: 0 }}
            exit={{ x: 50, transition: { duration: 0.1 } }}
          >
            {initialText}
          </motion.span>
        </motion.button>
      )}
    </AnimatePresence>
  );
};
