mnNumber:85},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-969b2bca-7e9c-4915-a1d7-0c277e1038ff.mdx",lineNumber:1,columnNumber:79},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-969b2bca-7e9c-4915-a1d7-0c277e1038ff.mdx",lineNumber:1,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-969b2bca-7e9c-4915-a1d7-0c277e1038ff.mdx",lineNumber:21,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-969b2bca-7e9c-4915-a1d7-0c277e1038ff.mdx",lineNumber:19,columnNumber:1},this),(0,n.jsxDEV)(B,{value:"manual",children:(0,n.jsxDEV)(O,{children:[(0,n.jsxDEV)(E,{children:"Copy and paste the following code into your project."},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-969b2bca-7e9c-4915-a1d7-0c277e1038ff.mdx",lineNumber:31,columnNumber:1},this),(0,n.jsxDEV)(w,{name:"blur-fade",children:(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import { useRef } from "react";
import {
  AnimatePresence,
  motion,
  useInView,
  UseInViewOptions,
  Variants,
} from "framer-motion";

type MarginType = UseInViewOptions["margin"];

interface BlurFadeProps {
  children: React.ReactNode;
  className?: string;
  variant?: {
    hidden: { y: number };
    visible: { y: number };
  };
  duration?: number;
  delay?: number;
  yOffset?: number;
  inView?: boolean;
  inViewMargin?: MarginType;
  blur?: string;
}

export default function BlurFade({
  children,
  className,
  variant,
  duration = 0.4,
  delay = 0,
  yOffset = 6,
  inView = false,
  inViewMargin = "-50px",
  blur = "6px",
}: BlurFadeProps) {
  const ref = useRef(null);
  const inViewResult = useInView(ref, { once: true, margin: inViewMargin });
  const isInView = !inView || inViewResult;
  const defaultVariants: Variants = {
    hidden: { y: yOffset, opacity: 0, filter: \`blur(\${blur})\` },
    visible: { y: -yOffset, opacity: 1, filter: \`blur(0px)\` },
  };
  const combinedVariants = variant || defaultVariants;
  return (
    <AnimatePresence>
      <motion.div
        ref={ref}
        initial="hidden"
        animate={isInView ? "visible" : "hidden"}
        exit="hidden"
        variants={combinedVariants}
        transition={{
          delay: 0.04 + delay,
          duration,
          ease: "easeOut",
        }}
        className={className}
      >
        {children}
      </motion.div>
    </AnimatePresence>
  );
}
