olumnNumber:85},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-3f22727e-3d34-4f8c-96e6-ee7eaae32a4e.mdx",lineNumber:1,columnNumber:79},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-3f22727e-3d34-4f8c-96e6-ee7eaae32a4e.mdx",lineNumber:1,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-3f22727e-3d34-4f8c-96e6-ee7eaae32a4e.mdx",lineNumber:21,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-3f22727e-3d34-4f8c-96e6-ee7eaae32a4e.mdx",lineNumber:19,columnNumber:1},this),(0,n.jsxDEV)(w,{value:"manual",children:(0,n.jsxDEV)(P,{children:[(0,n.jsxDEV)(g,{children:"Copy and paste the following code into your project."},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-3f22727e-3d34-4f8c-96e6-ee7eaae32a4e.mdx",lineNumber:31,columnNumber:1},this),(0,n.jsxDEV)(R,{name:"word-fade-in",children:(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import { motion, Variants } from "framer-motion";

import { cn } from "@/lib/utils";

interface WordFadeInProps {
  words: string;
  className?: string;
  delay?: number;
  variants?: Variants;
}

export default function WordFadeIn({
  words,
  delay = 0.15,
  variants = {
    hidden: { opacity: 0 },
    visible: (i: any) => ({
      y: 0,
      opacity: 1,
      transition: { delay: i * delay },
    }),
  },
  className,
}: WordFadeInProps) {
  const _words = words.split(" ");

  return (
    <motion.h1
      variants={variants}
      initial="hidden"
      animate="visible"
      className={cn(
        "font-display text-center text-4xl font-bold tracking-[-0.02em] text-black drop-shadow-sm dark:text-white md:text-7xl md:leading-[5rem]",
        className,
      )}
    >
      {_words.map((word, i) => (
        <motion.span key={word} variants={variants} custom={i}>
          {word}{" "}
        </motion.span>
      ))}
    </motion.h1>
  );
}
