olumnNumber:85},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-78b8aa6a-9f1e-41d5-a4d4-75ec0ca4c22a.mdx",lineNumber:1,columnNumber:79},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-78b8aa6a-9f1e-41d5-a4d4-75ec0ca4c22a.mdx",lineNumber:1,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-78b8aa6a-9f1e-41d5-a4d4-75ec0ca4c22a.mdx",lineNumber:21,columnNumber:1},this)},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-78b8aa6a-9f1e-41d5-a4d4-75ec0ca4c22a.mdx",lineNumber:19,columnNumber:1},this),(0,n.jsxDEV)(w,{value:"manual",children:(0,n.jsxDEV)(B,{children:[(0,n.jsxDEV)(g,{children:"Copy and paste the following code into your project."},void 0,!1,{fileName:"/home/spacious/projects/magicui/content/docs/components/_mdx_bundler_entry_point-78b8aa6a-9f1e-41d5-a4d4-75ec0ca4c22a.mdx",lineNumber:31,columnNumber:1},this),(0,n.jsxDEV)(P,{name:"word-pull-up",children:(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client";

import { motion, Variants } from "framer-motion";

import { cn } from "@/lib/utils";

interface WordPullUpProps {
  words: string;
  delayMultiple?: number;
  wrapperFramerProps?: Variants;
  framerProps?: Variants;
  className?: string;
}

export default function WordPullUp({
  words,
  wrapperFramerProps = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  },
  framerProps = {
    hidden: { y: 20, opacity: 0 },
    show: { y: 0, opacity: 1 },
  },
  className,
}: WordPullUpProps) {
  return (
    <motion.h1
      variants={wrapperFramerProps}
      initial="hidden"
      animate="show"
      className={cn(
        "font-display text-center text-4xl font-bold leading-[5rem] tracking-[-0.02em] drop-shadow-sm",
        className,
      )}
    >
      {words.split(" ").map((word, i) => (
        <motion.span
          key={i}
          variants={framerProps}
          style={{ display: "inline-block", paddingRight: "8px" }}
        >
          {word === "" ? <span>&nbsp;</span> : word}
        </motion.span>
      ))}
    </motion.h1>
  );
}
